function addItems(){$("#add-item-error").hide();var n=$("#add-item-title").val(),t=$("#add-item-due-at").val(),i={title:n,dueAt:t};$.post("ToDo/AddItem",i,function(){window.location="/ToDo"}).fail(function(n){if(n&&n.responseJSON){var t=Object.keys(n.responseJSON)[0],i=n.responseJSON[t];$("#add-item-error").text(i).show()}})}function markCompleted(n){n.disabled=!0;var t={id:n.name};$.post("ToDo/MarkDone",t,function(){var t=n.parentElement.parentElement;$(t).addClass("done")})}$(document).ready(function(){$("#add-item-button").on("click",addItems);$(".done-checkbox").on("click",function(n){markCompleted(n.target)})});